import React, { useState } from 'react';
import { Tooltip, Row, Col, Button, Select, Input, Popconfirm } from "antd";
import { VerticalAlignBottomOutlined,AppstoreAddOutlined } from "@ant-design/icons";
import Item from './Item';
import './css/styles.css';
const Table = (props) => {
    const { Option } = Select;
    const { Search } = Input;
    const [currentPage, setCurrentPage] = useState(1);
    const [perPage] = useState(8);
    const indexOfLast = currentPage * perPage;
    const indexOfFirst = indexOfLast - perPage;
    const pageNumbers = [];

    const department = [{
        deptid: 1,
        deptName: "dfdfdfdfd",
        startDate: "2020-02-02"
    },
    {
        deptid: 1,
        deptName: "dfdfdfdfd",
        startDate: "2020-02-02"
    }, {
        deptid: 1,
        deptName: "dfdfdfdfd",
        startDate: "2020-02-02"
    }
        , {
        deptid: 1,
        deptName: "dfdfdfdfd",
        startDate: "2020-02-02"
    }]
    for (var i = 1; i <= Math.ceil(department.length / perPage); i++) {
        pageNumbers.push(i);
    }
    const current = (a) => {
        setCurrentPage(a);
    };
    const showpage = pageNumbers.map((page, index) => {
        return (
            <Tooltip title={index + 1} key={index}>
                <li className="page-item">
                    <a className="page-link" onClick={() => current(index + 1)}>
                        {page}
                    </a>
                </li>
            </Tooltip>
        );
    });
    const currentDept = department.slice(indexOfFirst, indexOfLast);
    const employee1 = currentDept.map((e, index) => {
        return (
            <Item e={e} key={index} />
        );
    })

    const handleChange = (value) => {
        setHook(value);
    }
    const [hook, setHook] = useState([]);
    return (
        <div>
            <div className="container">
                <div className="card">
                    <div className="card-header">
                        <div className="row">
                            <div className="col-sm-4">

                            </div>
                            <div className="col-sm-4">
                                
                            </div>
                        </div>
                    </div>
                    <div className="card-body">
                        <div className="table-responsive-md">
                            <table className="table table-striped">
                                <thead>
                                    <tr>
                                        <th>#</th>
                                        <th>Department Id</th>
                                        <th>Department Name</th>
                                        <th>Start Date</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {
                                        employee1
                                    }
                                </tbody>
                            </table>
                        </div>
                    </div>
                    <ul className="pagination justify-content-center">
                        <li className="page-item disabled">
                            <a className="page-link" href="#" tabIndex="-1">
                                Previous
        </a>
                        </li>
                        {
                            showpage
                        }
                        <Tooltip title="Next :)">
                            <li className="page-item">
                                <a className="page-link" href="#">
                                    Next
          </a>
                            </li>
                        </Tooltip>
                    </ul>
                </div>
            </div>
        </div>
    );
}
export default Table;
