import React, { useState } from "react";
import { Menu, Switch, Button, Col, Row } from "antd";
import { Link } from "react-router-dom";
import "./css/styles.css";
import Media from "react-media";
import {
  CalendarOutlined,
  AppstoreOutlined,
  SettingOutlined,
  HomeOutlined,
  LogoutOutlined,
  BulbOutlined
} from "@ant-design/icons";
import Avatar from "antd/lib/avatar/avatar";
const Index = (props) => {
  const [current, setCurrent] = useState("home");
  const handleClick = (e) => {
    setCurrent(e.key);
  };
  const { SubMenu } = Menu;

  return (
    <div>
      <Media
        query="(max-width: 599px)"
        render={() => (
          <Menu style={{ width: 256 }} mode="horizontal">
            <Menu.Item key="1" icon={<HomeOutlined />}>
              Home
            </Menu.Item>
            <SubMenu key="sub1" icon={<AppstoreOutlined />} title="Department">
              <Menu.Item key="3">Employees</Menu.Item>
              <Menu.Item key="4">Positions</Menu.Item>
            </SubMenu>
            <SubMenu key="sub2" icon={<CalendarOutlined />} title="Date">
              <Menu.Item key="7">Holidays</Menu.Item>
              <Menu.Item key="8">Leave</Menu.Item>
              <Menu.Item key="9">Attandance</Menu.Item>
            </SubMenu>
            <SubMenu key="sub2" icon={<SettingOutlined />} title="Settings">
              <Menu.Item key="7">Option 7</Menu.Item>
              <Menu.Item key="8">Log out</Menu.Item>
            </SubMenu>
          </Menu>
        )}
      />

      <Media
        query="(min-width: 599px)"
        render={() => (
          <Menu
            style={{ width: 256 }}
            defaultSelectedKeys={["1"]}
            defaultOpenKeys={["sub1", "sub2"]}
            mode="inline"
          >
            <Menu.Item key="1" icon={<HomeOutlined />}>
              Home
            </Menu.Item>
            <SubMenu key="sub1" icon={<AppstoreOutlined />} title="Department">
              <Menu.Item key="3">Employees</Menu.Item>
              <Menu.Item key="4">Positions</Menu.Item>
            </SubMenu>
            <SubMenu key="sub2" icon={<CalendarOutlined />} title="Date">
              <Menu.Item key="7">Holidays</Menu.Item>
              <Menu.Item key="8">Leave</Menu.Item>
              <Menu.Item key="9">Attandance</Menu.Item>
            </SubMenu>
            <SubMenu key="sub3" icon={<SettingOutlined />} title="Settings">
              <Menu.Item key="10" icon={<BulbOutlined />} style={{color:"green"}}>
                <Switch></Switch>
              </Menu.Item>
              <Menu.Item key="11" icon={<LogoutOutlined />} style={{color:"blue"}}>
               Log out
              </Menu.Item>
            </SubMenu>
          </Menu>
        )}
      />
    </div>
  );
};

export default Index;
