import React from 'react';
import { Tag, Tooltip, Button, Modal, Form, Input, Select, DatePicker, Popconfirm, Statistic, Card, notification } from 'antd';
import { SolutionOutlined, EditOutlined, UsergroupDeleteOutlined, DeleteOutlined } from '@ant-design/icons';
import { useState } from 'react';
import { UserOutlined } from '@ant-design/icons';
import './css/styles.css';
const Item = (props) => {
    console.log(props.e);
    const [visible, setVisible] = useState(false);
    const [updateVisible, setUpdateVisible] = useState(false);
    const layout = {
        labelCol: { span: 6 },
        wrapperCol: { span: 18 },
    };
    const { Option } = Select;
    const toggleEmplVisible = () => {
        setVisible(true);
    }
    const toggleUpdateVisible = () => {
        setUpdateVisible(true);
    }
    const handleEmplCancel = () => {
        setVisible(false);
    }
    const handleUpdateCancel = () => {
        setUpdateVisible(false);
    }
    const onFinish = () => {
        setUpdateVisible(false);
        const args = {
            message: 'Updated Successfully',
            description:
                'This department was updated in Your System !',
            duration: 1,
        };
        notification.open(args);
    }
    return (
        <tr >
            <td></td>
            <td>{props.e.id}</td>
            <td>{props.e.name}</td>
            <td>
                <Tooltip title="Update here!">
                    <Button
                        shape="circle"
                        icon={<EditOutlined />}
                        onClick={toggleUpdateVisible}
                        style={{ backgroundColor: "rgb(107, 189, 243)" }}
                    />
                    <Modal
                        visible={updateVisible}
                        title="Update Department"
                        onCancel={handleUpdateCancel}
                        footer={[
                            <Button key="back" onClick={handleEmplCancel}>
                                Cancel
                      </Button>,
                            <Button key="submit" type="primary" onClick={onFinish}>
                                Update
                      </Button>
                        ]}
                    >
                        <Form {...layout}>
                            <Form.Item
                                label="Id"
                                name="deptId"
                                rules={[{ required: true }]}
                                hasFeedback>
                                <Input size="middle" />
                            </Form.Item>
                            <Form.Item
                                label="Name"
                                name="deptName"
                                rules={[{ required: true }]}
                                hasFeedback>
                                <Input size="middle" />
                            </Form.Item>
                            <Form.Item
                                label="StartDate"
                                name="startDate"
                                rules={[{ required: true }]}
                                hasFeedback>
                                <DatePicker size="middle" style={{ width: 354 }} />
                            </Form.Item>

                        </Form>
                    </Modal>
                </Tooltip>
                <Tooltip title="Delete">
                    <Popconfirm
                        placement="top"
                        title="Are you sure delete this position    !"
                    >
                        <Button
                            shape="circle"
                            icon={<DeleteOutlined />}
                            style={{ backgroundColor: "rgb(236, 118, 82)" }}
                        />
                    </Popconfirm>
                </Tooltip>
            </td>
        </tr>
    );
}

export default Item;
