import React from 'react';
import { Tag, Tooltip, Button, Modal, Form, Input, Select, DatePicker } from 'antd';
import { SolutionOutlined, ManOutlined, WomanOutlined, DeleteOutlined } from '@ant-design/icons';
import { useState } from 'react';
const Item = (props) => {
    const [visible, setVisible] = useState(false);
    const [addVisible, setAddVisible] = useState(false);
    const layout = {
        labelCol: { span: 6 },
        wrapperCol: { span: 18 },
    };
    const { Option } = Select;
    const toggleVisible = () => {
        setVisible(true);
    }
    const toggleAddVisible = () => {
        setAddVisible(true);
    }
    const handleCancel = () => {
        setVisible(false);
    }
    const handleAddCancel = () => {
        setAddVisible(false);
    }
    const check = (a) => {
        if (a === 1) {
            return (
                <Tag color="gold">You</Tag>
            );
        }
        if (a === 2) {
            return (
                <Tag color="">You</Tag>
            );
        }
    }
    return (
        <tr key={props.e.id}>
            <td>{props.e.emplid}</td>
            <td>{props.e.code}</td>
            <td>{props.e.firstName}</td>
            <td>
                {props.e.lastName}
            </td>
            <td>
                {
                    props.e.gender === "MALE" ? <Tag color="geekblue" icon={<ManOutlined />}></Tag> : <Tag color="orange" icon={<WomanOutlined />}></Tag>
                }
            </td>
            <td>{props.e.startDate}</td>
            <td>{props.e.endDate}</td>
            <td>{
                check
            }</td>
            <td>{props.e.modifyBy}</td>
            <td>
                <Tooltip title="Update!">
                    <Button
                        shape="circle"
                        icon={<SolutionOutlined />}
                        onClick={toggleAddVisible}
                        style={{ backgroundColor: "rgb(107, 189, 243)" }}
                    />
                    <Modal
                        visible={addVisible}
                        title="Employee Information"
                        onCancel={handleAddCancel}
                    >
                        <Form {...layout}>
                            <Form.Item
                                label="Employee Name"
                                name="employeeName"
                                hasFeedback>
                                <Input readOnly size="small" />
                            </Form.Item>
                            <Form.Item
                                label="Position"
                                name="position"
                                hasFeedback>
                                <Input readOnly size="small" />
                            </Form.Item>
                            <Form.Item
                                label="Deparment"
                                name="department"
                                hasFeedback>
                                <Input readOnly size="small" />
                            </Form.Item>

                        </Form>
                    </Modal>
                </Tooltip>
                <Tooltip title="Delete!">
                    <Button
                        shape="circle"
                        icon={<DeleteOutlined />}
                        onClick={toggleAddVisible}
                        style={{ backgroundColor: "rgb(236, 118, 82)" }}
                    />
                </Tooltip>
            </td>
        </tr>
    );
}

export default Item;
