import React from 'react';
import { Tag, Popconfirm, Tooltip, Button, Modal } from 'antd';
import { UserAddOutlined, FolderOpenOutlined } from '@ant-design/icons';
import { useState } from 'react';
const Item = (props) => {

    const [visible,setVisible]=useState(false);
    const makeColor = (a) => {
        if (a === 1) {
            return (
                <Tag color="orange">
                    CA1
                </Tag>
            );
        }
        if (a === 2) {
            return (
                <Tag color="geekblue">
                    CA2
                </Tag>
            );
        }
        if (a === 3) {
            return (
                <Tag color="lime">
                    CA3
                </Tag>
            );
        }
    }
    const Visible=()=>{
        setVisible(true);
    }
    return (
        <tr key={props.e.id}>
            <td>{props.e.key}</td>
            <td>{props.e.employeeCode}</td>
            <td>{props.e.employeeName}</td>
            <td>
                {props.e.department}
            </td>
            <td>{props.e.position}</td>
            <td>
                {
                    makeColor(props.e.workingShift)
                }
            </td>

            <td>{props.e.startDate}</td>
            <td>{props.e.endDate}</td>
            <td>
                <Tooltip title="His/her history">
                    <Button
                        className="update"
                        shape="circle"
                        icon={<FolderOpenOutlined />}
                        onClick={this.showModal}
                    />
                    <Modal
                        title="Working Shift History"
                        visible={visible}
                        okText="Close"
                        onCancel={handleCancel}
                    >
                        <p>Some contents...</p>
                        <p>Some contents...</p>
                        <p>Some contents...</p>
                    </Modal>
                </Tooltip>
                <Popconfirm
                    placement="top"
                    title='Are you sure to delete this employee?'
                    // onConfirm={onDelete}
                    htmlType="submit"
                    okText="Yes"
                    cancelText="No"
                >
                    <Tooltip title="Add here!">
                        <Button
                            className="delete"
                            shape="circle"
                            icon={<UserAddOutlined />}
                        />
                    </Tooltip>
                </Popconfirm>
            </td>
        </tr>
    );
}
export default Item;
