import React, { useState } from 'react';
import { Menu, Col, Row, Image, Avatar, Badge, Dropdown, Button, Input, Switch } from 'antd';
import { LogoutOutlined, ContactsOutlined } from '@ant-design/icons';
import { UnorderedListOutlined } from '@ant-design/icons';
import logo from '../Image/logo.png';
import { Link } from 'react-router-dom';
import { useEffect } from 'react';
function Header(props) {
    const { SubMenu } = Menu;
    const { Search } = Input;
    const [theme, setTheme] = useState(localStorage.getItem("theme"));

    const toggleTheme = () => {
        localStorage.getItem("theme") === 'light' ?  localStorage.setItem('theme','dark'):  localStorage.setItem('theme','light');
        //props.test(theme);
    }
    const menu = (
        <Menu>
            <Menu.Item key="0" icon={<ContactsOutlined />}>
                <Link to="">Profile</Link>
            </Menu.Item>
            <Menu.Item key="1" icon={<LogoutOutlined />}>
                <Link to="">Log out</Link>
            </Menu.Item>
        </Menu>
    );
    return (
        <div id="banner" style={{ height: 50 }}>
            <nav class="navbar navbar-expand-lg">
                <a class="navbar-brand" href="#">
                    <img src={logo} width={30} />
                </a>
                <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                    <span class="navbar-toggler-icon"></span>
                </button>

                <div class="collapse navbar-collapse" id="navbarSupportedContent">
                    <div className="row">
                        <div className="col-md-4">

                        </div>
                        <div className="col-md-4"></div>
                    </div>
                </div>
            </nav>
        </div>
    );
}

export default Header;
