import React, { useState } from "react";
import { Menu, Switch, Button, Col, Row } from "antd";
import { Link } from "react-router-dom";
import "./css/styles.css";
import Media from "react-media";
import {
  CalendarOutlined,
  AppstoreOutlined,
  SettingOutlined,
  HomeOutlined,
  LogoutOutlined,
  BulbOutlined
} from "@ant-design/icons";
const MenuBar = (props) => {
  const [theme, setTheme] = useState('dark');
  localStorage.setItem('theme', theme);
  const a = localStorage.getItem('theme');
  const toggleTheme = () => {
    theme === 'light' ? setTheme('dark') : setTheme('light');
  }
  const { SubMenu } = Menu;

  return (
    <div>
      <Media
        query="(max-width: 599px)"
        render={() => (
          <Menu mode="horizontal">
            <Menu.Item key="1" icon={<HomeOutlined />}>
              <Link to="/home">Home</Link>
            </Menu.Item>
            <SubMenu key="sub1" icon={<AppstoreOutlined />} title="Infomations">
              <Menu.Item key="3" >
                <Link to="/employee" style={{ color: "blue" }}>Employees</Link>
              </Menu.Item>
              <Menu.Item key="4"><Link to="/department" style={{ color: "blue" }}>Departments</Link></Menu.Item>
              <Menu.Item key="5"><Link to="/empshift" style={{ color: "blue" }}>Working Shift Management</Link></Menu.Item>
              <Menu.Item key="6"><Link to="/position" style={{ color: "blue" }}>Positions</Link></Menu.Item>
            </SubMenu>
            <SubMenu key="sub2" icon={<CalendarOutlined />} title="Date">
              <Menu.Item key="7"><Link to="/holidays" style={{ color: "blue" }}>Holidays</Link></Menu.Item>
              <Menu.Item key="8">Leave</Menu.Item>
              <Menu.Item key="9">Attandance</Menu.Item>
            </SubMenu>
            <SubMenu key="sub3" icon={<SettingOutlined />} title="Settings">
              <Menu.Item key="10" icon={<BulbOutlined />} style={{ color: "green" }}>
                <Switch onClick={toggleTheme}></Switch>
              </Menu.Item>
              <Menu.Item key="11" icon={<LogoutOutlined />} style={{ color: "blue" }}>
                Log out
              </Menu.Item>
            </SubMenu>
          </Menu>
        )}
      />

      <Media
        query="(min-width: 599px)"
        render={() => (
          <Menu
            defaultSelectedKeys={[`${props.e}`]}
            defaultOpenKeys={["sub1", "sub2", "sub3"]}

            mode="inline"
            theme={a}
            id="bannerleft"
          >
            <Menu.Item key="1" id="home" icon={<HomeOutlined />}>
              <Link to="/home" id="home">Home</Link>
            </Menu.Item>
            <SubMenu key="sub1" icon={<AppstoreOutlined />} title="Infomations">
              <Menu.Item key="3" >
                <Link to="/employee">Employees</Link>
              </Menu.Item>
              <Menu.Item key="4"><Link to="/department" >Departments</Link></Menu.Item>
              <Menu.Item key="5"><Link to="/empshift" >Working Shift Management</Link></Menu.Item>
              <Menu.Item key="6"><Link to="/position" >Positions</Link></Menu.Item>
            </SubMenu>
            <SubMenu key="sub2" icon={<CalendarOutlined />} title="Date">
              <Menu.Item key="7"><Link to="/holidays" >Holidays</Link></Menu.Item>
              <Menu.Item key="8" >Leave</Menu.Item>
              <Menu.Item key="9" >Attandance</Menu.Item>
            </SubMenu>
            <SubMenu key="sub3" icon={<SettingOutlined />} title="Settings">
              <Menu.Item key="10" icon={<BulbOutlined />}>
                <Switch onChange={toggleTheme}></Switch>
              </Menu.Item>
              <Menu.Item key="11" icon={<LogoutOutlined />} >
                Log out
              </Menu.Item>
            </SubMenu>
          </Menu>
        )}
      />
    </div>
  );
};

export default MenuBar;
