import React, { useRef } from 'react';
import { Tag, Tooltip, Button, Modal, Form, Input, Select, DatePicker, Popconfirm, Statistic, Card, notification } from 'antd';
import { SolutionOutlined, EditOutlined, UsergroupDeleteOutlined, DeleteOutlined } from '@ant-design/icons';
import { useState } from 'react';
import { UserOutlined } from '@ant-design/icons';
import './css/styles.css';
import { useEffect } from 'react';
import PositionService from '../../Services/PositionServices';
const Item = (props) => {
    //console.log(props.e);
    const [visible, setVisible] = useState(false);
    const [updateVisible, setUpdateVisible] = useState(false);
    const id=useRef();
    const name=useRef();
    const layout = {
        labelCol: { span: 6 },
        wrapperCol: { span: 18 },
    };
    const { Option } = Select;
    const [form] = Form.useForm();

    const toggleUpdateVisible = () => {
        setUpdateVisible(true);
    }
    const handleEmplCancel = () => {
        setVisible(false);
    }
    const handleUpdateCancel = () => {
        setUpdateVisible(false);
    }
    const onFinish = () => {

        const position={
            pos_ID:id,
            positionName:name
        }
        PositionService.update(props.e.pos_ID,{}).then(res=>{
            console.log(res.status);
        })
    }
    useEffect(()=>{
        form.setFieldsValue({
            pos_ID:props.e.pos_ID,
            positionName:props.e.positionName
        });
    },);
    return (
        <tr key={props.e.pos_ID}>
            <td>{props.e.pos_ID}</td>
            <td>{props.e.positionName}</td>
            <td>
                <Tooltip title="Update!">
                    <Button
                        shape="circle"
                        icon={<EditOutlined />}
                        onClick={toggleUpdateVisible}
                        style={{ backgroundColor: "rgb(107, 189, 243)" }}
                    />
                    <Modal
                        visible={updateVisible}
                        title="Update Position"
                        onCancel={handleUpdateCancel}
                        footer={[
                            <Button key="back" onClick={handleEmplCancel}>
                                Cancel
                      </Button>,
                            <Button key="submit" type="primary" onClick={onFinish}>
                                Update
                      </Button>
                        ]}
                    >
                        <Form {...layout}  form={form}>
                            <Form.Item
                                label="Id"
                                name="pos_ID"
                                rules={[{ required: true }]}
                                hasFeedback>
                                <Input size="middle" ref={id}/>
                            </Form.Item>
                            <Form.Item
                                label="Name"
                                name="positionName"
                                rules={[{ required: true }]}
                                hasFeedback>
                                <Input size="middle" ref={name}/>
                            </Form.Item>
                        </Form>
                    </Modal>
                </Tooltip>
                <Tooltip title="Delete">
                    <Popconfirm
                        placement="top"
                        title="Are you sure delete this position    !"
                    >
                        <Button
                            shape="circle"
                            icon={<DeleteOutlined />}
                            style={{ backgroundColor: "rgb(236, 118, 82)" }}
                        />
                    </Popconfirm>
                </Tooltip>
            </td>
        </tr>
    );
}

export default Item;
