import React, { useState, useEffect } from 'react';
import { Tooltip, Row, Col, Button, Select, Input, Popconfirm, Form, Modal, DatePicker, notification } from "antd";
import { VerticalAlignBottomOutlined, BankOutlined } from "@ant-design/icons";
import Item from './Item';
import PositionServices from '../../Services/PositionServices';
const Table = (props) => {
    const { Option } = Select;
    const { Search } = Input;
    const [currentPage, setCurrentPage] = useState(1);
    const [perPage] = useState(8);
    const indexOfLast = currentPage * perPage;
    const indexOfFirst = indexOfLast - perPage;
    const [modal, setModal] = useState(false);
    const [positions, setPositions] = useState();
    useEffect(() => {
        PositionServices.list().then(res => {
            setPositions(res.data);
        })
    },[])

    const pageNumbers = [];
    console.log(positions);

   
    const layout = {
        labelCol: { span: 6 },
        wrapperCol: { span: 18 },
    };
    const onFinish = () => {
        setModal(false)
        const args = {
            message: 'Created Successfully',
            description:
                'This department was created in Your System !',
            duration: 1,
        };
        notification.open(args);
    }
    const toggleModal = () => {
        setModal(true);
    }
    const handleCancel = () => {
        setModal(false);
    }


    const handleChange = (value) => {
        setHook(value);
    }
    const [hook, setHook] = useState([]);
    return (
        <div>
            <div className="container">
                <div className="card">
                    <div className="card-header">
                        <div className="row">
                            <div className="col-sm-4">
                            </div>
                            <div className="col-sm-4">
                            </div>
                            <div className="col-sm-4">
                                <Tooltip placement="topRight" title="Export excel !">
                                    <Popconfirm title="Do you export to excel!">
                                        <Button icon={<VerticalAlignBottomOutlined />} type="primary" id="exportdept">
                                            Export
                                    </Button>
                                    </Popconfirm>
                                </Tooltip>
                                <Tooltip placement="topRight" title="Create!">
                                    <Button icon={<BankOutlined />} type="primary" id="addept" onClick={toggleModal}>
                                        Create
                            </Button>
                                </Tooltip>
                                <Modal
                                    visible={modal}
                                    title="Create Position"
                                    onCancel={handleCancel}
                                    footer={[
                                        <Button key="back" onClick={handleCancel}>
                                            Cancel
                      </Button>,
                                        <Button key="submit" type="primary" onClick={onFinish}>
                                            Create
                      </Button>
                                    ]}
                                >
                                    <Form {...layout}>
                                        <Form.Item
                                            label="Id"
                                            name="deptId"
                                            rules={[{ required: true }]}
                                            hasFeedback>
                                            <Input size="middle" />
                                        </Form.Item>
                                        <Form.Item
                                            label="Name"
                                            name="deptName"
                                            rules={[{ required: true }]}
                                            hasFeedback>
                                            <Input size="middle" />
                                        </Form.Item>
                                    </Form>
                                </Modal>

                            </div>
                        </div>
                    </div>
                    <div className="card-body">
                        <div className="table-responsive-md">
                            <table className="table table-striped">
                                <thead>
                                    <tr>
                                        <th>#</th>
                                        <th>Id</th>
                                        <th>Position Name</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    
                                </tbody>
                            </table>
                        </div>
                    </div>
                    <ul className="pagination justify-content-center">
                        <li className="page-item disabled">
                            <a className="page-link" href="#" tabIndex="-1">
                                Previous
    </a>
                        </li>
                       
                        <Tooltip title="Next :)">
                            <li className="page-item">
                                <a className="page-link" href="#">
                                    Next
      </a>
                            </li>
                        </Tooltip>
                    </ul>
                </div>
            </div>
        </div>
    );
}

export default Table;
