import React, { useState } from "react";
import { Menu, Switch, Button } from "antd";
import { Link } from "react-router-dom";
import "./css/styles.css";
import Media from "react-media";
import {
  MailOutlined,
  CalendarOutlined,
  AppstoreOutlined,
  SettingOutlined,
  MenuUnfoldOutlined,
  MenuFoldOutlined,
} from "@ant-design/icons";
const Index = (props) => {
  const { SubMenu } = Menu;
  const [collapsed, setCollapsed] = useState(false);
  const [theme, setTheme] = useState("light");
  const [mode,setMode]=useState("inline");

  const changeTheme = () => {
    theme === "light" ? setTheme("dark") : setTheme("light");
  };

  const changeMode = () => {
    mode === "inline" ? setMode("vertical") : setMode("inline");
  };

  const toggleCollapsed = () => {
    collapsed === false ? setCollapsed(true) : setCollapsed(false);
  };

  return (
    <div>
      <Media
        query="(max-width:700px)"
        render={() => (
          <Menu inlineCollapsed={collapsed} theme={theme} mode="horizontal">
            <Menu.Item key="1" icon={<MailOutlined />}>
              <Link to="/">L</Link>
            </Menu.Item>
            <Menu.Item key="2" icon={<CalendarOutlined />}>
              Navigation Two
            </Menu.Item>
            <SubMenu
              key="sub1"
              icon={<AppstoreOutlined />}
              title="Navigation Two"
            >
              <Menu.Item key="3">Option 3</Menu.Item>
              <Menu.Item key="4">Option 4</Menu.Item>
              <SubMenu key="sub1-2" title="Submenu">
                <Menu.Item key="5">Option 5</Menu.Item>
                <Menu.Item key="6">Option 6</Menu.Item>
              </SubMenu>
            </SubMenu>
            <SubMenu
              key="sub2"
              icon={<SettingOutlined />}
              title="Navigation Three"
            >
              <Menu.Item key="7">Option 7</Menu.Item>
              <Menu.Item key="8">Option 8</Menu.Item>
              <Menu.Item key="9">Option 9</Menu.Item>
              <Menu.Item key="10">Option 10</Menu.Item>
            </SubMenu>
            <SubMenu key="sub3" icon={<SettingOutlined />} title="Cài Đặt">
              <Menu.Item key="11">
                <Switch
                  onChange={changeTheme}
                  checked={theme === "light"}
                  checkedChildren="Light"
                  unCheckedChildren="Dark"
                />
              </Menu.Item>
            </SubMenu>
          </Menu>
        )}
      />
      <Media
        query="(min-width:700px)"
        render={() => (
          <Menu inlineCollapsed={collapsed} theme={theme} mode={mode}>
            <Menu.Item key="1" icon={<MailOutlined />}>
              <Link to="/">L</Link>
            </Menu.Item>
            <Menu.Item key="2" icon={<CalendarOutlined />}>
              Navigation Two
            </Menu.Item>
            <SubMenu
              key="sub1"
              icon={<AppstoreOutlined />}
              title="Navigation Two"
            >
              <Menu.Item key="3">Option 3</Menu.Item>
              <Menu.Item key="4">Option 4</Menu.Item>
              <SubMenu key="sub1-2" title="Submenu">
                <Menu.Item key="5">Option 5</Menu.Item>
                <Menu.Item key="6">Option 6</Menu.Item>
              </SubMenu>
            </SubMenu>
            <SubMenu
              key="sub2"
              icon={<SettingOutlined />}
              title="Navigation Three"
            >
              <Menu.Item key="7">Option 7</Menu.Item>
              <Menu.Item key="8">Option 8</Menu.Item>
              <Menu.Item key="9">Option 9</Menu.Item>
              <Menu.Item key="10">Option 10</Menu.Item>
            </SubMenu>
            <SubMenu key="sub3" icon={<SettingOutlined />} title="Cài Đặt">
              <Menu.Item key="11">
                <Switch
                  onChange={changeTheme}
                  checked={theme === "light"}
                  checkedChildren="Light"
                  unCheckedChildren="Dark"
                />
              </Menu.Item>
              <Menu.Item key="11">
                <Switch
                  onChange={changeMode}
                  checked={mode === "inline"}
                  checkedChildren="Inline"
                  unCheckedChildren="Dark"
                />
              </Menu.Item>
            </SubMenu>
            <Menu.Item>
              <Button
                type="text"
                onClick={toggleCollapsed}
                style={{ marginBottom: 16 }}
              >
                {React.createElement(
                  collapsed ? MenuUnfoldOutlined : MenuFoldOutlined
                )}
              </Button>
            </Menu.Item>
          </Menu>
        )}
      />
    </div>
  );
};

export default Index;
