import React from "react";
import { Form, Input, Button, Row, Col, Card, Image, Tooltip } from "antd";
import "antd/dist/antd.css";
import "./css/index.css";
import "../Image/LimitedPhysicalEnglishsetter-small.gif";
const Index = (props) => {
  const layout = {
    labelCol: { span: 6 },
    wrapperCol: { span: 18 },
  };
  const tailLayout = {
    wrapperCol: { offset: 6, span: 18 },
  };
  const { Meta } = Card;
  return (
    <div className="container">
       
      <div className="login">
        <Row>
            <Col flex></Col>
            <Col flex={3}><h5>HR website</h5></Col>
            <Col flex={1}></Col>
        </Row>
        <Card>
          <Row>
            <Col flex={2}>
              <div className="background">
                <Image
                  width={400}
                  height={250}
                  src="https://thumbs.gfycat.com/LimitedPhysicalEnglishsetter-small.gif"
                />
              </div>
            </Col>
            <Col flex={3}>
              <Form {...layout}>
                <Form.Item
                  label="Username"
                  name="username"
                  rules={[{ required: true }]}
                >
                  <Input placeholder="Input your username" />
                </Form.Item>
                <Form.Item
                  label="Password"
                  name="password"
                  rules={[{ required: true }]}
                >
                  <Input.Password placeholder="Input your password" />
                </Form.Item>
                <Form.Item {...tailLayout}>
                  <Tooltip
                  placement="topLeft"
                  title="Login Here !"
                  >
                    <Button htmlType="submit" type="primary">
                      Login
                    </Button>
                  </Tooltip>
                </Form.Item>
              </Form>
            </Col>
          </Row>
        </Card>
      </div>
    </div>
  );
};
export default Index;
