import React from 'react';
import { Tag, Tooltip, Button, Modal, Form, Input, Select, DatePicker, Popconfirm } from 'antd';
import { SolutionOutlined, EditOutlined, UsergroupDeleteOutlined, DeleteOutlined } from '@ant-design/icons';
import { useState } from 'react';
const Item = (props) => {
    const [visible, setVisible] = useState(false);
    const [addVisible, setAddVisible] = useState(false);
    const layout = {
        labelCol: { span: 6 },
        wrapperCol: { span: 18 },
    };
    const { Option } = Select;
    const toggleVisible = () => {
        setVisible(true);
    }
    const toggleAddVisible = () => {
        setAddVisible(true);
    }
    const handleCancel = () => {
        setVisible(false);
    }
    const handleAddCancel = () => {
        setAddVisible(false);
    }
    return (
        <tr key={props.e.id}>
            <td></td>
            <td>{props.e.deptid}</td>
            <td>{props.e.deptName}</td>
            <td>{props.e.startDate}</td>
            <td>
                <Tooltip title="Update here!">
                    <Button
                        shape="circle"
                        icon={<EditOutlined />}
                        onClick={toggleAddVisible} />
                    <Modal
                        visible={addVisible}
                        title="Employee Information"
                        onCancel={handleAddCancel}
                    >
                        <Form {...layout}>
                            <Form.Item
                                label="Employee Name"
                                name="employeeName"
                                hasFeedback>
                                <Input readOnly size="small" />
                            </Form.Item>
                            <Form.Item
                                label="Position"
                                name="position"
                                hasFeedback>
                                <Input readOnly size="small" />
                            </Form.Item>
                            <Form.Item
                                label="Deparment"
                                name="department"
                                hasFeedback>
                                <Input readOnly size="small" />
                            </Form.Item>

                        </Form>
                    </Modal>
                </Tooltip>

                <Tooltip title="Employee">
                    <Button
                        shape="circle"
                        icon={<UsergroupDeleteOutlined />}
                        onClick={toggleAddVisible} />
                    <Modal
                        visible={addVisible}
                        title="Employee Information"
                        onCancel={handleAddCancel}
                    >
                        <Form {...layout}>
                            <Form.Item
                                label="Department Id"
                                name="deptId"
                                hasFeedback>
                                <Input  size="small" />
                            </Form.Item>
                            <Form.Item
                                label="Name"
                                name="deptName"
                                hasFeedback>
                                <Input  size="small" />
                            </Form.Item>
                            <Form.Item
                                label="StartDate"
                                name="startDate"
                                hasFeedback>
                               <DatePicker size="small"/>
                            </Form.Item>

                        </Form>
                    </Modal>
                </Tooltip>

                <Tooltip title="Delete">
                    <Popconfirm
                        placement="top"
                        title="Are you sure delete this department!"
                    >
                        <Button
                            shape="circle"
                            icon={<DeleteOutlined />}
                        />
                    </Popconfirm>
                </Tooltip>
            </td>
        </tr>
    );
}

export default Item;
