import React from 'react';
import { Tag, Popconfirm, Tooltip, Button } from 'antd';
import { UserAddOutlined, FolderOpenOutlined, ManOutlined } from '@ant-design/icons';
const Item = (props) => {
    const makeColor = (a) => {
        if (a === 1) {
            return (
                <Tag color="orange">
                   CA1
                </Tag>
            );
        }
        if (a === 2) {
            return (
                <Tag color="geekblue">
                   CA2
                </Tag>
            );
        }
        if (a === 3) {
            return (
                <Tag color="lime">
                   CA3
                </Tag>
            );
        }
    }
    return (
        <tr key={props.e.id}>
            <td>{props.e.key}</td>
            <td>{props.e.employeeCode}</td>
            <td>{props.e.employeeName}</td>
            <td>
                {props.e.department}
            </td>
            <td>{props.e.position}</td>
            <td>
                {
                    makeColor(props.e.workingShift)
                }
            </td>

            <td>{props.e.startDate}</td>
            <td>{props.e.endDate}</td>
            <td>
                <Tooltip title="his/her history">
                    <Button
                        className="update"
                        shape="circle"
                        icon={<FolderOpenOutlined />}
                        //onClick={handle}
                        data-toggle="modal"
                        data-target="#updatestudent"
                    />
                </Tooltip>
                <Popconfirm
                    placement="top"
                    title='Are you sure to delete this employee?'
                    // onConfirm={onDelete}
                    htmlType="submit"
                    okText="Yes"
                    cancelText="No"
                >
                    <Tooltip title="Add here!">
                        <Button
                            className="delete"
                            shape="circle"
                            icon={<UserAddOutlined />}
                        />
                    </Tooltip>
                </Popconfirm>
            </td>
        </tr>
    );
}
export default Item;
