import React,{useState} from 'react';
import { Tooltip, Row, Col, Button, Select, Input, Popconfirm} from "antd";
import { VerticalAlignBottomOutlined } from "@ant-design/icons";
import Item from './Item';
const Table=(props)=> {
    const { Option } = Select;
    const { Search } = Input;
    const [currentPage, setCurrentPage] = useState(1);
    const [perPage] = useState(8);
    const indexOfLast = currentPage * perPage;
    const indexOfFirst = indexOfLast - perPage;
    
      const pageNumbers = [];
      for (var i = 1; i <= Math.ceil(department.length / perPage); i++) {
        pageNumbers.push(i);
      }
      const current = (a) => {
        setCurrentPage(a);
      };
      const showpage = pageNumbers.map((page, index) => {
        return (
          <Tooltip title={index + 1} key={index}>
            <li className="page-item">
              <a className="page-link" onClick={() => current(index + 1)}>
                {page}
              </a>
            </li>
          </Tooltip>
        );
      });
      const currentDept = department.slice(indexOfFirst, indexOfLast);
      const employee = currentDept.map((e, index) => {
        return (
          <Item e={e} key={index} />
        );
      })
    
      const handleChange = (value) => {
        setHook(value);
      }
      const [hook, setHook] = useState([]);
    return (
        <div>
            <div className="container">
      <div className="card">
        <div className="card-header">
          <Row>
            <Col span={8}>
              <h5>Department:</h5>
              <Select
                showArrow
                mode="multiple"
                onChange={handleChange}
                placeholder="Select A Department"
                size="small"
                style={{ width: 250 }}
              >
                <Option value="1">1</Option>
                <Option value="2">2</Option>
                <Option value="3">3</Option>
                <Option value="4">4</Option>
                <Option value="5">5</Option>
              </Select>
            </Col>
            <Col span={8} offset={8}>
              <Tooltip placement="topRight" title="Export excel !">
                <Popconfirm title="Do you export to excel!">
                  <Button icon={<VerticalAlignBottomOutlined />} type="primary" id="export">
                    Export
              </Button>
                </Popconfirm>
              </Tooltip>
            </Col>
          </Row>

        </div>
        <div className="card-body">
          <div className="tools">
            <div className="row">
              <Search
                placeholder="Input employee name "
                onSearch={value => console.log(value)}
                style={{ width: 200 }}
                size="small"
              />
              <Select
                showArrow
                mode="multiple"
                onChange={handleChange}
                placeholder="Select A Position"
                size="small"
                style={{ width: 200 ,marginLeft:10,marginRight:10}}
              >
                <Option value="1">1</Option>
                <Option value="2">2</Option>
                <Option value="3">3</Option>
                <Option value="4">4</Option>
                <Option value="5">5</Option>
              </Select>

              <Select
                showArrow
                mode="multiple"
                onChange={handleChange}
                placeholder="Select A Shift"
                size="small"
                style={{ width: 200 }}
                id="shiftselect"
              >
                <Option value="1">1</Option>
                <Option value="2">2</Option>
                <Option value="3">3</Option>
                <Option value="4">4</Option>
                <Option value="5">5</Option>
              </Select>

            </div>
          </div>
          <div className="table-responsive-md">
            <table className="table table-striped">
              <thead>
                <tr>
                  <th>#</th>
                  <th>Employee Code</th>
                  <th>Employee Name</th>
                  <th>Department</th>
                  <th>Position</th>
                  <th>Working Shift</th>
                  <th>Start Date </th>
                  <th>End Date</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {
                  employee
                }
              </tbody>
            </table>
          </div>
        </div>
        <ul className="pagination justify-content-center">
          <li className="page-item disabled">
            <a className="page-link" href="#" tabIndex="-1">
              Previous
            </a>
          </li>
          {
            showpage
          }
          <Tooltip title="Next :)">
            <li className="page-item">
              <a className="page-link" href="#">
                Next
              </a>
            </li>
          </Tooltip>
        </ul>
      </div>
    </div>
        </div>
    );
}

export default Table;
