import React from 'react';
import {Tag,Popconfirm,Tooltip,Button} from 'antd';
import {UserAddOutlined,FolderOpenOutlined,} from '@ant-design/icons';
const Item = (props) => {
    const makeColor=()=>{
        if(props.e.workingShift===1){
            return(
                <Tag color="blue">
              <ManOutlined />
            </Tag>
            );
        }
        if(props.e.workingShift===2){
            return(
                <Tag color="geekblue">
              <ManOutlined />
            </Tag>
            );
        }
        if(props.e.workingShift===3){
            return(
                <Tag color="lime">
              <ManOutlined />
            </Tag>
            );
        }
    }
    return (
            <tr key={props.e.id}>
                <td>{props.e.key}</td>
                <td>{props.e.employeeCode}</td>
                <td>{props.e.employeeName}</td>
                <td>
                    {props.e.department}
                </td>
                <td>{props.e.position}</td>
                <td>
                   {
                     makeColor
                   }
                </td>
               
                <td>{props.e.startDate}</td>
                <td>{props.e.endDate}</td>
                <td>
                    <Tooltip title="his/her history">
                        <Button
                            className="update"
                            shape="circle"
                            icon={<FolderOpenOutlined/>}
                            //onClick={handle}
                            data-toggle="modal"
                            data-target="#updatestudent"
                        />
                    </Tooltip>
                    <Popconfirm
                        placement="top"
                        title='Are you sure to delete this employee?'
                       // onConfirm={onDelete}
                        htmlType="submit"
                        okText="Yes"
                        cancelText="No"
                    >
                        <Tooltip title="delete here">
                            <Button
                                className="delete"
                                shape="circle"
                                icon={<UserAddOutlined />}
                            />
                        </Tooltip>
                    </Popconfirm>
                </td>
            </tr>
    );
}
export default Item;
