import React from "react";
import { Form, Input, Button, Row, Col,Card,Image } from "antd";
import "antd/dist/antd.css";
import "./css/index.css";
const Index = (props) => {
  const layout = {
    labelCol: { span: 6 },
    wrapperCol: { span: 18 },
  };
  const tailLayout = {
    wrapperCol: { offset: 6, span: 18 },
  };
  const { Meta } = Card;
  return (
    <div className="container">
      <div className="login">
        <Card>
          <Row>
            <Col flex={3}>
              <div className="background" >
                 <Image src="https://thumbs.gfycat.com/LimitedPhysicalEnglishsetter-small.gif"></Image>
              </div>
            </Col>
            <Col flex={2}>
              <Form {...layout}>
                <Form.Item
                  label="Username"
                  name="username"
                  rules={[{ required: true }]}
                >
                  <Input placeholder="Input your username" />
                </Form.Item>
                <Form.Item
                  label="Password"
                  name="password"
                  rules={[{ required: true }]}
                >
                  <Input.Password placeholder="Input your password" />
                </Form.Item>
                <Form.Item {...tailLayout}>
                  <Button htmlType="submit" type="primary">
                    Login
                  </Button>
                </Form.Item>
              </Form>
            </Col>
          </Row>
        </Card>
      </div>
    </div>
  );
};
export default Index;
